import React from 'react';
import './App.css';

import { useState } from "react";

import Game from "./classes/Game";

import Instructions from "./components/Instructions";
import InputForm from "./components/InputForm";
import ScorePanel from "./components/ScorePanel";
import Clues from "./components/Clues";
import Timeline from "./components/Timeline";
import GameOver from "./components/GameOver";

import TimelineContext from "./store/TimelineContext";

function App() {
  let [game, setGame] = useState(Game.startingGame());

  function resetGame(newGame) {
    setGame(prevGame => new Game(newGame));
  }

  let gameContext = { get: game, set: resetGame };


  return (
    <TimelineContext.Provider value={gameContext}>
      <div className="App">
        <header>
          <h1>Chronoquiz</h1>
          <Instructions />
          <InputForm  />
          { game.isGameOver ? null : <ScorePanel /> }
        </header>
        <main>
          { game.isGameOver ? <GameOver /> : <Clues /> }
          <Timeline />
        </main>
      </div>
    </TimelineContext.Provider>
  );
}

export default App;
