import debug from "../lib/debug";
import BackendRequest from "../classes/BackendRequest";

// TODO could you include the token as a member?
interface UserInput {
  username: string,
  password: string,
  timelineList: array<number>; // ID numbers of Django Timeline instances
} 

export default class User {
  username: string;
  password: string;
  timelineList: array<number>;

  constructor({ username = "", password = "" }: UserInput = {}) {
    this.username = username;
    this.password = password;
  }

  get isEmpty(): boolean {
    return !Object.values(this).some(v => v);
  }

  json(): string {
    return JSON.stringify(this);
  }

  async exists(): boolean {
    let answer = false;
    let request = new BackendRequest({
      url: "check_user/", 
      method: "POST", 
      bodyObject: this
    });
    let response = await request.fetch();
    if (response.ok) {
      answer = true;
    } else {
      debug("User not found");
    }
    return answer;
  }

  async register(): boolean {
    let answer = false;
    let request = new BackendRequest({
      url: "register/", 
      method: "POST", 
      bodyObject: this
    });
    let response = await request.fetch();
    if (response.ok) {
      answer = true;
    } else {
      debug("Could not register user");
    }
    return answer;
  }

  async authenticate(): string {
    let token = null;
    let request = new BackendRequest({
      url: "login/", 
      method: "POST", 
      bodyObject: this
    });
    let response = await request.fetch();
    if (response.ok) {
      let json = await response.json();
      token = json.token;
      debug(`Authenticated user ${this.username}`);
    } else {
      debug(`Problem authenticating user ${this.username}: Response status ${response.status}, ${response.statusText}`);
    }
    return token;
  }

}
