import Card from "./Card";
import { default as Color } from "./RgbColorMix";

export default class FactList extends Array {

  constructor(cards: Array<Card> = []) {
    if (cards.every(c => c instanceof Card)) {
      super(...cards);
    } else {
      throw new Error("Can only create FactList from an array of Card instances");
    }
  }

  // PRIVATE METHODS

  // Set the colors of the cards in this list, in chronological order, to
  // evenly spaced intervals along the spectrum.
  #setColors(): FactList {
    this.sortByDate();

    this.forEach((card, index) => {
      this[index].color = Color.colorAtIndex(index, this.length, 
                                              Color.SPECTRUM);
    });

    return this;
  } 

  // Shuffle the array, using the Fisher-Yates/Knuth shuffle
  // (`https://en.wikipedia.org/wiki/Fisher%E2%80%93Yates_shuffle`)
  #shuffle(): FactList {

    function randomInt(max: number): number {
      return Math.floor(Math.random() * max);
    } 

    for (let i = this.length - 1; i > 0; --i) {
      let j = randomInt(i);
      [this[i], this[j]] = [this[j], this[i]];
    }

    return this;
  }

  // PUBLIC METHODS

  // Sort the array by the date field, ascending.
  sortByDate(): FactList {
    this.sort((c1, c2) => { return c1.date - c2.date });
    return this;
  }

  setupClues() {
    this.#setColors();
    this.#shuffle();
  }

  isEmpty(): boolean {
    return this.length === 0;
  }

  allButLast(): FactList {
    return this.slice(0, -1);
  }

  last(): Card {
    return this.at(-1);
  }

  dropLast(): FactList {
    this.pop();
    return this;
  }

  prepend(item): FactList {
    this.unshift(item);
    return this;
  }

  // Add event to array and then resort by date.
  addFact(card) {
    this.push(card);
    this.sortByDate();
  }

  resetMargins(): FactList {
    this.forEach(c => c.expand = false);
    return this;
  }

  addAnswer(answer): FactList {
    return this.prepend(answer).sortByDate().resetMargins();
  }
}
