export default function Create() {

  function Instructions() {
    return(
      <p className="instructions">Your data will not be saved until you click Save.</p>
    );
  }

  function MetadataPanel() {
    return(
      <section id="timeline">
        <h2>Set Up This Timeline</h2>
        <form onSubmit={createTimeline}>
          <label htmlFor="title">Title</label>
          <input type="text" name="title" />

          <label htmlFor="description">Description</label>
          <input type="text" name="description" />

          <label htmlFor="keywords">Keywords</label>
          <input type="text" name="keywords" />
          <label htmlFor="creator">Creator (for public display)</label>
          <input type="text" name="creator" />


          <button type="submit">Create Empty Timeline</button>
        </form>
      </section>
    );
  }

  function CurrentEventsPanel() {
    return(
      <section id="current">
        <h2>Events on This Timeline</h2>
      </section>
    );
  }

  function NewEventForm() {
    return(
      <section id="new">
        <h2>Add an Event</h2>
      </section>
    );
  }

  function SubmitButton() {
    return(
      <button type="submit">Save</button>
    );
  }

  return(
    <main>
      <h1>Create a Chronoquiz</h1>
      <Instructions />
      <MetadataPanel />
      <CurrentEventsPanel />
      <NewEventForm />
      <SubmitButton />
    </main>
  );
}
