import { useContext } from "react";
import TimelineContext from "../store/TimelineContext";

export default function ScorePanel() {
  let context = useContext(TimelineContext);
  let score = context.get.score;
  let clues = context.get.clues;

  function restart() {
    console.log("Restart");
    location.reload();

  }

  if (clues && !clues.isEmpty()) {
    return(
      <>
        <div id="score">
          <p>Score: <span className="score">{score}</span></p>
        </div>
        <button id="restart" onClick={restart}>Play Again</button>
      </>
    );
  }
}
