import { Link } from "react-router-dom";

export default function TimelineList({ data, type = "game" }) {

  const listButtonVisibility = "show";

  function AdminLink({ target }) {

    function DeleteButton() {

      let [itemToDelete, setItemToDelete] = useState(null)

      function deleteItem(event) {
        setItemToDelete(target)
        console.log("Delete");
      }

      return(
        <button type="button" 
          className={listButtonVisibility} 
          onClick={deleteItem}>ðŸ—™</button>
      );
    }

    let href = `../game/${target.id}`;

    return(
      <li key={target.id}>
        <Link to={href}>{target.title}</Link>
        <DeleteButton />
      </li>
    );
  }

  function GameLink({ target }) {
    let href = `../game/${target.id}`;
    return(
      <li key={target.id}>
        <Link to={href}>{target.title} 
          (<code>{target.username}</code>)</Link>
      </li>
    );
  }

  let ThisLink = type === "admin" ? AdminLink : GameLink;

  return(
    <ul>
      {data.map(item => <ThisLink key={item.id} target={item} />)}
    </ul>
  );
}
