import { useContext } from "react";
import TimelineContext from "../store/TimelineContext";
import Card from "./Card";

/** CSS value for card's normal left margin when shifted right */
const CARD_LEFT_MARGIN_DEFAULT = "var(--card-margin)";
const CARD_LEFT_MARGIN_EXTRA = `calc(5 * ${CARD_LEFT_MARGIN_DEFAULT})`;

// TODO
// function setCardLeftMargin(el, length) {
//   el.style.marginLeft = length;
// }

function removeTimelineGap(el: HTMLElement): void {
  setCardLeftMargin(el, CARD_LEFT_MARGIN_DEFAULT);
}

function dragleaveHandler(event: React.DragEvent<HTMLElement>): void {
  let el = event.target;
  if (isCardElement(el)) {
    removeTimelineGap(el);
  }
}

let dragDropInterface = {
  onDrop: dropHandler,
  onDragOver: dragoverHandler,
  onDragLeave: dragleaveHandler
}


export default function Clues() {
  let context = useContext(TimelineContext);
  let game = context.get;
  let timeline = game.timeline;

  function isCardElement(el: HTMLElement): boolean {
    return el.className === "card";
  }

  if (game.isActive && timeline && !timeline.isEmpty()) {
    return(
      <div className="timeline" {...dragDropInterface}>
        {timeline.items.map(card => <Card key={card.id}>{card}</Card>)}
      </div>
    );
  }
}
