import { useContext, useEffect } from "react";
import UserContext from "../store/UserContext";
import User from "../classes/User";

export default function Login() {
  let userContext = useContext(UserContext);
  let currentUser = userContext.get("currentUser");
  let setCurrentUser = userContext.set("currentUser");
  let setUserToken = userContext.set("userToken");
  let setAuthenticated = userContext.set("authenticated");

  function login(event) {
    event.preventDefault();
    let username = event.target.username.value;
    let email    = event.target.email.value;
    let password = event.target.password.value;
    setCurrentUser(new User({
      username: username,
      email: email,
      password: password
    }));
  }

  useEffect(() => {
    async function authenticate(user) {
      let response = await fetch("http://127.0.0.1:8000/login/", {
        method: "POST",
        headers: new Headers({
          "Accept": "application/json",
          "Content-Type": "application/json"
        }),
        body: user.json()
      });
      if (response.ok) {
        let json = await response.json();
        let token = json.token;
        setUserToken(token);
        setAuthenticated(true);
        console.debug(`Authenticated user ${user.username}`);
      } else {
        console.debug(`Problem authenticating user ${user.username}: Response status ${response.status}, ${response.statusText}`);
      }
    }
    if (currentUser && !currentUser.isEmpty && !currentUser.authenticated) {
      authenticate(currentUser);
    }
  }, [currentUser]);


  return(
    <main>
      <h1>Log In</h1>
      <form id="login" onSubmit={login}>
        <div className="formInputBlock">
          <div className="formItem">
            <label htmlFor="username">Username</label>
            <input type="text" name="username" />
          </div>
          <div className="formItem">
            <label htmlFor="email">Email</label>
            <input type="email" name="email" />
          </div>
          <div className="formItem">
            <label htmlFor="password">Password</label>
            <input type="password" name="password" />
          </div>
        </div>
        <button type="submit">Log In</button>
      </form>
    </main>
  );
}
