import { Link } from "react-router-dom";

export default function TimelineList({ data, hideUser = false }) {

  function AdminLink({ target }) {
    let username = hideUser ? null : <> (<code>{target.username}</code>)</>;

    return(
      <li key={target.id}>
        <Link to={href}>{target.title}</Link>
      </li>
    );
  }

  function GameLink({ target }) {
    let href = `../game/${target.id}`;

    return(
      <li key={target.id}>
        <Link to={href}>{target.title} 
          (<code>{target.username}</code>)</Link>
      </li>
    );
  }

  return(
    <ul>
      {data.map(item => <GameLink target={item} />)}
    </ul>
  );
}
