import { useContext } from "react";
import TimelineContext from "../store/TimelineContext";
import Card from "./Card";
import FactList from "../classes/FactList";

export default function Clues() {
  let context = useContext(TimelineContext);
  let game = context.get;
  let clues = game.clues;
  console.log(clues);
  if (!clues.every(c => c instanceof Card)) console.debug("Not all cards");

  function Stub(card: Card): Array<React.ReactElement> {
    return(
      <div key={card.id} className="cardStub" />
      );
  }

  if (clues && !clues.isEmpty()) {
    console.debug(clues);
    console.debug(typeof clues);
    console.debug(clues instanceof FactList);

    let current = clues.pop();
    console.debug(current);
    console.debug(current instanceof Card);

    return(
      <div className="clueDeck">
        {clues.map(Stub)}
        <Card>{current}</Card>
      </div>
    );
  }
}
