import Fact from "./Fact";

interface CardInput  {
  isClue: boolean;      // Is this a clue (true) or answer?
  event: Fact;
}

export default class Card {
  isClue: boolean;
  id: string;
  event: Fact;
  color: string;    // CSS color to be used in timeline
  expand: boolean;  // Add margin because a card is being dragged over?
  flash: boolean;   // Should this card flash an alert? (For wrong guess)
  #safe: boolean;   // Has this card been sanitized?

  // Each card gets the given info and a random unique identifier.
  constructor({ isClue = true, event = new Fact(), color = null, expand = false, flash = false }: CardInput) {
    this.isClue = isClue;
    this.id = crypto.randomUUID();
    this.event = event;
    this.color = color;
    this.expand = expand;
    this.flash = flash;
    this.#safe = false; // Has this card been sanitized?
  }

  markSafe(): Card {
    this.#safe = true;
    return this;
  }

  // PUBLIC METHODS

  get isSafe(): boolean {
    return this.#safe;
  }

  /** Creat a new card with sanitized input.
   * - The date must be an integer string <= the current year (including
   *   negative numbers).
   * - The info is converted to plain text using textContent.
   * - The image, if present, is downloaded and cached.
   *
   * The parameters are the same as for new Card().
   *
   * Returns: Card with validated content (with safe property set to true), or
   * null if the input was invalid.
   */
  static async newSafeCard({ isClue, date, info, img }: 
                           CardInput): Card | null {
    let card = null;
    try {
      let cleanDate = Card.#sanitizeDate(date);
      let cleanInfo = info;
      let cleanImg = await Card.#sanitizeImg(img).catch(console.error);

      // The date is the only dealbreaker. We just skip a bad image link.
      if (cleanDate) {
        card = new Card({
          isClue: isClue, 
          event: new Fact({ date: cleanDate, info: cleanInfo, img: cleanImg})
        });
        card.markSafe();
        return card;
      } else {
        throw new Error(`Could not sanitize card input with date '${date}', info '${info}'`);
      }
    } catch(e) {
      console.error(e);
    }
    return card;
  }

  json() {
    return this.event.json();
  }

  // PRIVATE METHODS
  // Sanitize input 
  static #sanitizeDate(rawDate: any): Date | null {
    let date = null;
    try {
      let numTest = Number(rawDate);
      if (!isNaN(numTest) 
          && Number.isInteger(numTest) 
          && numTest <= new Date().getFullYear()) {

            // TODO date comparison bad for cards with current year, because
            // they include exact time if created after Now card?
            date = new Date();
            date.setFullYear(numTest);
        } else {
          throw new Error(`Bad date input ${rawDate}`);
        }
    } catch(e) { 
      console.error(e);
    }
    return date;
  }

  static async #sanitizeImg(url: string): string | null {
    function getImageIfExists(url: string): Promise { 
      return new Promise((resolve, reject) => {
        const img = new Image();
        img.src = url;
        img.onload = () => resolve(true);
        img.onerror = () => resolve(false);
      });
    }
    let testedUrl = null;
    try {
      if (url) {
        let imgTest = await getImageIfExists(url).catch(console.log);
        if (imgTest === true) {
          testedUrl = url;
        } else {
          throw new Error(`Image not found at url '${url}'`);
        }
      } 
    } catch(e) {
      console.error(e);
    }
    return testedUrl;
  }

  /**
   * Return the year if positive or year BC if negative. (Deals with the year
   * only.) 
   *
   * Technically BC should be offset by one year but we told users to use
   * negative numbers as years BC.
   *
   * Returns: Formatted string for year, with BC if the year was
   * negative
   */
  dateToString(): string { 
    if (this.isClue) {
      return "Clue";
    } else {
      let yearZero = new Date();
      yearZero.setFullYear(0);

      let displayYear = this.event.year;
      if (this.event.date < yearZero) {
        displayYear = `${-displayYear} bce`; 
      } 
      return displayYear;
    }
  }

  // Return the date as YYYY year string.
  get year(): string { 
    return this.event.year; 
  }

  copyAsAnswer(): Card {
    return new Card({ ...this, isClue: false});
  }

  flash(): Card {
    this.flash = true;
    return this;
  }
}
