import Card from "./Card";
import FactList from "./FactList";

export default class Game {
  clues: FactList;
  timeline: FactList;
  score: number;

  constructor({ clues, timeline, score = 0 }: {
    clues?: FactList, 
    timeline?: FactList, 
    score?: number
  }) {
    this.clues = clues;
    this.timeline = timeline;
    this.score = score;
  }

  static startingGame() {
    let NowCard = new Card({
      isClue: false, 
      date: new Date(),
      info: "Now", 
      img: "./img/hourglass.jpg",
      color: "violet" // TODO RGB
    });

    return new Game({
      clues: new FactList(), 
      timeline: new FactList([NowCard]),
      score: 0
    });
  }

  get isActive() {
    return this.clues && !this.clues.isEmpty();
  }
}
