import debug from "../lib/debug";
import BackendRequest from "../classes/BackendRequest";

interface UserInput {
  username: string,
  email: string,
  password: string 
} 

export default class User {
  username: string;
  email: string;
  password: string;

  constructor(
    { username = "",
      email    = "",
      password = "" }: UserInput = {}
  ) {
    this.username = username;
    this.email = email;
    this.password = password;
  }

  get isEmpty(): boolean {
    return !Object.values(this).some(v => v);
  }

  json(): string {
    return JSON.stringify(this);
  }

  async exists(): boolean {
    let answer = false;
    let response = await BackendRequest.fetch({
      url: "check_user/", 
      method: "POST", 
      bodyObject: this
    });
    if (response.ok) {
      answer = true;
    } else {
      debug("User not found");
    }
    return answer;
  }

  async register(): boolean {
    let answer = false;
    let response = await BackendRequest.fetch({
      url: "register/", 
      method: "POST", 
      bodyObject: this
    });
    if (response.ok) {
      answer = true;
    } else {
      debug("Could not register user");
    }
    return answer;
  }

  async authenticate(): string {
    let token = null;
    let response = await BackendRequest.fetch({
      url: "login/", 
      method: "POST", 
      bodyObject: this
    });
    if (response.ok) {
      let json = await response.json();
      token = json.token;
      debug(`Authenticated user ${this.username}`);
    } else {
      debug(`Problem authenticating user ${this.username}: Response status ${response.status}, ${response.statusText}`);
    }
    return token;
  }

}
