import { useContext, useState, useEffect } from "react";
import { Link } from "react-router-dom";

import User from "../classes/User";
import TimelineList from "../components/TimelineList";

export default function Choose() {
  let [timelineList, setTimelineList] = useState([]);

  useEffect(() => {
    async function loadTimelineList() {
      let response = await fetch(`${User.SERVER}/timelines/`);
      if (response.ok) {
        let json = await response.json();
        setTimelineList(json);
      }
    }

    loadTimelineList();
  }, []);

  function Timelines() {
    function timelineLink(item) {
      return(
        <li key={item.id}>
          <Link to={`../game/${item.id}`}>{item.title}</Link>
        </li>
      );
    }

    return(
      <ul>
        {timelineList.map(timelineLink)}
      </ul>
    );
  }

  return(
    <main>
      <h1>Choose a Quiz</h1>
      <Timelines data={timelineList} />
    </main>
  );
}
