import Card from "./Card";
import FactList from "./FactList";
import { default as Color } from "./RgbColorMix";

interface GameInput {
  clues: FactList;
  timeline: FactList;
  score: number;
}

export default class Game {
  clues: FactList;
  timeline: FactList;
  score: number;
  isActive: boolean;
  isGameOver: boolean;

  constructor({ 
    clues, 
    timeline, 
    score = 0, 
    isActive = false, 
    isGameOver = false 
  }: GameInput) {

    this.clues = clues;
    this.timeline = timeline;
    this.score = score;
    this.isActive = isActive;
    this.isGameOver = isGameOver;
  }

  /** Remove last item of clue list and add it to timeline list.
   * (Note: The timeline list will then shuffle itself in chronological
   * order.)
   */
  moveCurrentClueToTimeline(): Game {
    let answer = this.clues.pop();
    answer.isClue = false;
    this.timeline.addFact(answer);
    return this;
  }

  resetTimelineMargins(): Game {
    this.timeline.resetMargins();
    return this;
  }

  setClues(clues: FactList): Game {
    this.clues = new FactList(...clues);
    return this;
  }

  static updateClues(clues: FactList): (Game) => Game {
    return function(prevGame) {
      return new Game(prevGame.setClues(clues));
    }
  }

  setTimeline(timeline: FactList): Game {
    this.timeline = new FactList(...timeline);
    return this;
  }

  // Start the game with just a "Now" card in the timeline.
  incrementScore(): Game {
    ++this.score;
    return this;
  }


  static decrementScore(score) {
    return Math.max(0, score - 1);
  }

  activate(): Game {
    this.isActive = true;
    return this;
  }

  // Since we have the image locally we don't need to sanitize the card.
  static startingGame(): Game {
    let NowCard = new Card({
      isClue: false, 
      date: new Date(),
      info: "Now", 
      img: "./img/hourglass.jpg",
      color: Color.VIOLET
    });
    NowCard.markSafe();

    return new Game({
      clues: new FactList(), 
      timeline: new FactList(NowCard),
      score: 0
    });
  }

  clone(): Game {
    new Game(this);
  }

}
