import React from "react";
import "./App.css";

import { useState, useReducer } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import Layout from "./components/shared/Layout";
import About from "./routes/About";
import Admin from "./routes/Admin";
import Choose from "./routes/Choose";
import Chronoquiz from "./routes/Chronoquiz";

import User from "./classes/User";
import Game from "./classes/Game";

import UserContext from "./store/UserContext";
import TimelineContext from "./store/TimelineContext";

const defaultUserState = {
  currentUser: new User(),
  authenticated: false,
  userToken: null
}

function userReducer(state, action) {
  console.debug(state);
  console.debug(action);

  let obj = action.payload;
  let newState = null;

  switch(action.type) {
    case "set":
      newState = { ...state, ...obj.state };
    break;

    case "user":
      newState = { ...state, currentUser: obj };
    break;

    case "authenticated":
      newState = { ...state, authenticated: true };
    break;

    case "deauthenticated":
      newState = { ...state, authenticated: false };
    break;

    case "token":
      newState = { ...state, userToken: obj.userToken };
    break;

    case "reset":
      newState = defaultUserState;
    break;

    default:
      newState = state;
  }

  return newState;
}

function App() {

  let [userState, dispatchUser] = useReducer(userReducer, defaultUserState);

  let userContext = {
    get: userState,
    set: dispatchUser
  };

  let timelineContext = {
    get:      key => timelineContext[key][0],
    set:      key => timelineContext[key][1],
    timeline: useState(new Game())
  };

  // TODO user-readable url like this?
  // <Route path="/game/:username/:gameTitle" element={<Chronoquiz />} />

  return (
    <UserContext.Provider value={userContext}>
      <TimelineContext.Provider value={timelineContext}>
        <BrowserRouter>
          <Routes>
            <Route path="/" element={<Layout />}>
              <Route path="/about" element={<About />} />
              <Route path="/admin" element={<Admin />} />
              <Route path="/game" element={<Choose />} />
              <Route path="/game/:gameId" element={<Chronoquiz />} />
            </Route>
          </Routes>
        </BrowserRouter>
      </TimelineContext.Provider>
    </UserContext.Provider>
  );
}

export default App;
