interface BackendRequestInput {
  url: string;
  method: string;
  token: string;
  bodyObject: object
}

export default class BackendRequest {
  url: string;
  fullUrl: string;
  method: string;
  bodyObject: object;
  authorization: string;

  constructor({ 
    url, method, bodyObject = {}, token = null
  }: BackendRequestInput) {

    this.url = url
    this.method = method;
    this.authorization = token ? `Authorization: Token ${token}` : null;
    this.bodyObject = bodyObject;
  }

  static SERVER = "http://127.0.0.1:8000";

  get fullUrl(): string {
    return `${BackendRequest.SERVER}/${this.url}`;
  }

  async fetch() {
    let response = await fetch(this.fullUrl, {
      method: this.method,
      headers: new Headers({
        "Content-Type": "application/json",
        "Accept": "application/json",
        ...this.authorization
      }),
      body: JSON.stringify(this.bodyObject)
    });
    return response;
  }

}
