import { useState } from "react";

import Card from "../classes/Card";
import FactList from "../classes/FactList";

export default function Create() {

  let [timeline, setTimeline] = useState(null);

  function Instructions() {
    return(
      <p className="instructions">Your data will not be saved until you click Save.</p>
    );
  }

  function MetadataPanel() {
    function createTimeline(event) {
      event.preventDefault();
      console.debug(`Create timeline with title ${event.target.title.value}`);
    }

    return(
      <section id="timeline">
        <h2>Set Up This Timeline</h2>
        <form onSubmit={createTimeline}>
          <div className="formInputBlock">
            <div className="formItem">
              <label htmlFor="title">Title</label>
              <input type="text" name="title" />
            </div>
            <div className="formItem">
              <label htmlFor="description">Description</label>
              <input type="text" name="description" />
            </div>
            <div className="formItem">
              <label htmlFor="keywords">Keywords (separated with semicolons)</label>
              <input type="text" name="keywords" />
            </div>
            <div className="formItem">
              <label htmlFor="creator">Creator (for public display)</label>
              <input type="text" name="creator" />
            </div>
          </div>
          <button type="submit">Create Empty Timeline</button>
        </form>
      </section>
    );
  }

  function CurrentEventsPanel() {
    return(
      <section id="current">
        <h2>Events on This Timeline</h2>
      </section>
    );
  }

  function NewEventForm() {
    return(
      <section id="new">
        <h2>Add an Event</h2>
      </section>
    );
  }

  function SubmitButton() {
    return(
      <button type="submit">Save</button>
    );
  }

  return(
    <main>
      <h1>Create a Chronoquiz</h1>
      <Instructions />
      <MetadataPanel />
      <CurrentEventsPanel />
      <NewEventForm />
      <SubmitButton />
    </main>
  );
}
